scene.onOverlapTile(SpriteKind.Player, assets.tile`myTile33`, function (sprite, location) {
    if (selectedlvl == currentlvl) {
        currentlvl += 1
    }
    world(selectedlvl)
})
controller.left.onEvent(ControllerButtonEvent.Pressed, function () {
    if (selectinglvl) {
        selectedlvl += -1
        tiles.placeOnRandomTile(mySprite2, tileset[selectedlvl])
    }
})
controller.right.onEvent(ControllerButtonEvent.Pressed, function () {
    if (selectinglvl) {
        selectedlvl += 1
        tiles.placeOnRandomTile(mySprite2, tileset[selectedlvl])
    }
})
controller.A.onEvent(ControllerButtonEvent.Released, function () {
    if (!(selectinglvl)) {
        controller.moveSprite(mySprite, 100, 0)
        sprites.destroyAllSpritesOfKind(SpriteKind.shield)
        cancanjump = true
    }
})
scene.onHitWall(SpriteKind.runner, function (sprite, location) {
    if (sprite.tileKindAt(TileDirection.Right, assets.tile`myTile31`)) {
        sprites.destroy(sprite)
        music.play(music.createSoundEffect(
        WaveShape.Sawtooth,
        5130,
        0,
        255,
        0,
        200,
        SoundExpressionEffect.None,
        InterpolationCurve.Linear
        ), music.PlaybackMode.InBackground)
        timer.after(200, function () {
            music.play(music.createSoundEffect(
            WaveShape.Sawtooth,
            3130,
            0,
            255,
            0,
            200,
            SoundExpressionEffect.None,
            InterpolationCurve.Linear
            ), music.PlaybackMode.InBackground)
            timer.after(200, function () {
                music.play(music.createSoundEffect(
                WaveShape.Sawtooth,
                1130,
                0,
                255,
                0,
                400,
                SoundExpressionEffect.None,
                InterpolationCurve.Linear
                ), music.PlaybackMode.InBackground)
            })
        })
    }
})
controller.menu.onEvent(ControllerButtonEvent.Pressed, function () {
    blockSettings.clear()
    game.reset()
})
